mapper.select:
select count(id) as fileCount,count(DISTINCT roll_id) as rollCount from archives_2

1=1

  and date between #{start,jdbcType=VARCHAR} and #{end,jdbcType=VARCHAR}

select count(id) as fileCount,count(DISTINCT roll_id) as rollCount,file_type as fileType from archives_2

  1=1

    and date between #{start,jdbcType=VARCHAR} and #{end,jdbcType=VARCHAR}


group by file_type;

select

from archives_2

   1=1

    and id like #{id,jdbcType=VARCHAR}


    and file_id like #{fileId,jdbcType=VARCHAR}


    and  response_person like #{responsePerson,jdbcType=VARCHAR}


    and  title like #{title,jdbcType=VARCHAR}


    and   level = #{level,jdbcType=VARCHAR}


    and date between #{start,jdbcType=VARCHAR} and #{end,jdbcType=VARCHAR}


    and summary like CONCAT('%',#{summary},'%')


    and	note like CONCAT('%',#{note},'%')


    and  page_count = #{pageCount,jdbcType=INTEGER}


    and    file_type = #{fileType,jdbcType=VARCHAR}


    and  secret_date = #{secretDate,jdbcType=VARCHAR}


    and  file_color = #{fileColor,jdbcType=VARCHAR}


    and  concat(virtual_id,id,file_id,response_person,title,note,level,roll_id) like #{concatField,jdbcType=VARCHAR}



  limit #{page},#{constant}

select count(*) from archives_2

  1=1

    and id like #{id,jdbcType=VARCHAR}


    and file_id like #{fileId,jdbcType=VARCHAR}


    and  response_person like #{responsePerson,jdbcType=VARCHAR}


