mapper.select:
    select 

    from archives


			virtual_id =#{virtualId}


			id =#{id}


		and file_id=#{fileId}


		and level=#{level}


		and date>=#{start}


		and date<=#{end}


		and summary like CONCAT('%',#{summary},'%')


		and	note like CONCAT('%',#{note},'%')


	limit #{page},#{constant}

    select count(*) from archives


			virtual_id =#{virtualId}


			id =#{id}


		and file_id=#{fileId}


		and level=#{level}


		and date>=#{start}


		and date<=#{end}


		and	summary like CONCAT('%',#{summary},'%')


		and	note like CONCAT('%',#{note},'%')
